import React,{useEffect,useState} from 'react';
import DeleteDetail from './DeleteDetail';

import "./DeleteDetail.css";

function DeleteBooking() {

    const [formDetails,setFormDetails]=useState([]);
    const [isDeleted,setIsDeleted]=useState(false);
     
    useEffect(()=>{
            const getAllDetails=async()=>{
                try{
                    const response=await fetch("http://localhost:5000/api/bookings/bookingdetail");
                    const responseData = await response.json();
                    if (!response.ok) {
                        throw new Error(responseData.message);
                      }
                      else {
                        setFormDetails(responseData.bookingDetail);
                        console.log(responseData.bookingDetail);
                      }
                }catch(err){
                    console.log(err.message);
                }
            };

            getAllDetails();


    },[]);

    const deleteBooking=async(id)=>{
      try{
        const response=await fetch("http://localhost:5000/api/bookings/deletebooking",{
            method: "DELETE",
            body: JSON.stringify({
                 id: id
              }),
              headers: {
                'Content-Type': 'application/json'
              }
        });
        const responseData = await response.json();

        
        if (!response.ok) {
            throw new Error(responseData.message);
          }

          setFormDetails(prevValue => {
            return prevValue.filter((items,index) => {
                return items.id!=id;
              });
          });
          setIsDeleted(true);
          
    }catch(err){
        console.log(err.message);
    }
    }  

  return (
    <div style={{width: "90%", margin: "0 auto"}}>
        { formDetails.map((detail,index)=>{
            return <DeleteDetail key={index} name={detail.name} id={detail.id} startDate={detail.startdate} endDate={detail.endDate} floor={detail.floor} onDelete={deleteBooking} />;
        }) }
    </div>
  )
}

export default DeleteBooking;
