import React,{useState} from 'react';
import { Link } from 'react-router-dom';
import "./Login_Signup.css";

function UserLogin() {

    const [userLoginDetail,setUserLoginDetail]=useState({
        useremail: "",
        password: ""
    });

    const userHandler=(event)=>{
        const {name,value}=event.target;
        setUserLoginDetail((prevValue)=>{
            return {
                ...prevValue,
                [name]: value
            }
            
        });
    }

    const loginSubmitHandler=(event)=>{
        event.preventDefault();
        console.log(userLoginDetail.useremail);
        console.log(userLoginDetail.password);
    }
  return (
    <div className="form-handler1">
        <form onSubmit={loginSubmitHandler}>
        <div className="control-group1">
           <div className="form-control1">
              <label>Email</label>
              <input type="email" name="useremail" value={userLoginDetail.useremail} onChange={userHandler} required />
             </div> 
             <div className="form-control1">
              <label>Password</label>
              <input type="password" name="password" value={userLoginDetail.password} onChange={userHandler} required />
            </div> 
            </div>
            <div className="form-actions1">
           <button>Login</button>
           </div>  
            </form>
            <div className="form-actions2">
            <Link to="/user/signup" className="button2">Signup</Link>
           </div>
            </div>
  )
}

export default UserLogin;
