import React from 'react'

function Input() {

    const [proffesion,setProfession]=useState("");

    const professionHandler=(event)=>{
        setProfession(event.target.value);
    }

  return (
    <div>
            <input id="employee" type="radio" value="employee" onChange={professionHandler} />
            <label htmlFor="employee" Employee />
            
            <input id="pensioner" type="radio" value="pensioner" onChange={professionHandler} />
            <label htmlFor="pensioner"  Pensioner/>
            
            <input id="others" type="radio" value="others" onChange={professionHandler} />
            <label htmlFor="others"  Others/>
    </div>
  )
}

export default Input;
