import React,{useContext} from 'react';
import { useHistory,Link } from "react-router-dom";
import { AuthContext } from '../Context/auth-context';

function Admindashboard() {
    const auth=useContext(AuthContext);

    const history=useHistory();

    const createHandler=(event)=>{
        event.preventDefault();
        history.push("/input");

    }

    const readHandler=(event)=>{
        event.preventDefault();
        history.push("/admin/read");
    }

    const deleteHandler=(event)=>{
        event.preventDefault();
        history.push("/admin/delete");
    }

    const signupHandler=(event)=>{
        event.preventDefault();
        history.push("/admin/signup");
    }

    const logoutHandler=(event)=>{
        history.push("/");
        auth.logout();
    }

  return (
    <>
    <div className="home1">
        <button style={{border: "none", backgroundColor: "#004975" ,color: "#fff",float: "right",height: "100%"}} onClick={logoutHandler}>Logout</button>
    </div>
    <div style={{ display: "flex",flexDirection: "column",margin: "40px auto",}}>
    <div>
    <button onClick={createHandler}>Book Community Hall</button>
        </div>
        
        <div>
        <button onClick={readHandler}>All Booking Details</button>
        </div>
       
        <div>
        <button onClick={deleteHandler}>Delete a Booking</button>
        </div>
        
        <div>
        <button onClick={signupHandler}>Add a New Admin</button>
        </div>
       
    </div>
    </>
  )
}

export default Admindashboard;
