import React,{useState} from 'react';
import Form1 from './Form1';
import Form2 from './Form2';
import Form3 from './Form3';
import "./form.css";

function Input() {

    const [profession,setProfession]=useState("");

    const professionHandler=(event)=>{
        setProfession(event.target.value);
    }

    console.log(profession);

  return (
    <div>
      <div className="form-handler">
    <div className="control-group">
          <div className="form-control">
            <input id="employee" type="radio" name="selector" value="employee" onChange={professionHandler} />
            <label htmlFor="employee">Employee</label>
          </div>
          <div className="form-control">
            <input id="pensioner" type="radio" name="selector" value="pensioner" onChange={professionHandler} />
            <label htmlFor="pensioner">Pensioner</label>
          </div>
          <div className="form-control">
            <input id="others" type="radio" name="selector" value="others" onChange={professionHandler} />
            <labels htmlFor="others">Others</labels>
          </div>
      </div>

            {profession==="employee" && <Form1 />}
            {profession==="pensioner" && <Form2 />}
            {profession==="others" && <Form3 />}
    </div>
    </div>

  )
}

export default Input;
