import React,{useState} from 'react';
import { DateRangePicker } from 'react-date-range';
import { addDays,format } from 'date-fns';
import 'react-date-range/dist/styles.css'; 
import 'react-date-range/dist/theme/default.css'; 
import "./form.css";

function Form1() {

      const [empDetail,setEmpDetail]=useState({
           empName: "",
           empPost: "",
           empId: "",
           city: "",
           district: "",
           state: "",
           pincode: "",
           mobile: "",
           purpose: ""
      });

      const [openDateRange,setOpenDateRange]=useState(false);

      const [range, setRange] = useState([
        {
          startDate: new Date(),
          endDate: addDays(new Date(), 2),
          key: 'selection'
        }
      ]);

      const [floor,setFloor]=useState("");
      const [personDetail,setPersonDetail]=useState({
            personName: "",
            personAge: "",
            personId: "",
            personRelation: ""
      });

    const empHandler=(event)=>{
        const {name,value}=event.target;
        setEmpDetail((prevValue)=> {
            return {
            ...prevValue,
            [name]: value
            };
        });
    }
    const inputDateHandler=(event)=>{
      setOpenDateRange((prevState)=>!prevState);
    }

    const floorHandler=(event)=>{
        setFloor(event.target.value);
    }

    const personHandler=(event)=>{
        const {name,value}=event.target;
              setPersonDetail((prevValue)=> {
                return {
                    ...prevValue,
                    [name]: value
                }
              })
    }
  return (
    <div className="form-handler">
        <form>
        <div className="control-group">
           <div className="form-control">
              <label>Employee Name</label>
              <input type="text" name="empName" value={empDetail.empName} onChange={empHandler} />
             </div> 
             <div className="form-control">
              <label>Designation:</label>
              <input type="text" name="empPost" value={empDetail.empPost} onChange={empHandler} />
            </div> 

            <div className="form-control">
            <label>Employee Id:</label>
            <input type="text" name="empId" value={empDetail.empId} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label >Mobile No:</label>
            <input type="text" name="mobile" value={empDetail.mobile} onChange={empHandler} />
           </div>
           <div className="form-control">
            <label>Purpose</label>
            <input type="text" name="purpose" value={empDetail.purpose} onChange={empHandler} />
            </div>
            {/* <label>Current Posting Address:</label> */}
            <div className="form-control">
            <label >City:</label>
            <input  type="text" name="city" value={empDetail.city} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label>District</label>
            <input type="text" name="district" value={empDetail.district} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label>State</label>
            <input type="text" name="state" value={empDetail.state} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label>Pincode</label>
            <input  type="text" name="pincode" value={empDetail.pincode} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label>Select Date</label>
            <input  type="text" name="date" value={`${format(range[0].startDate,'dd/MM/yyyy')} to ${format(range[0].endDate,'dd/MM/yyyy')}`} onClick={inputDateHandler} />
            <div className="calendarwrap">
            {openDateRange && <DateRangePicker
              onChange={item => setRange([item.selection])}
              showSelectionPreview={true}
              moveRangeOnFirstSelection={false}
              months={2}
              minDate={addDays(new Date(),0)}
              maxDate={addDays(new Date(),120)}
              allowedmaxDays={3}
              ranges={range}
              direction="horizontal"
              className='calendarElement'
            />}
            </div>
            
            </div>
            {/* <label>Select Floor</label> */}
            <div className="form-control">
            <label>Hall  (Ground Floor):</label>
            <input type="radio" name="selectFloor" value="hall" onChange={floorHandler} />
            </div>
            <div className="form-control">
            <label>Lawn (First Floor):</label>
            <input type="radio" name="selectFloor" value="lawn" onChange={floorHandler} />
            </div>
            <div className="form-control">
            <label>Both Hall and Lawn</label>
            <input type="radio" name="selectFloor" value="both" onChange={floorHandler} />
            </div>
            
            {/* <label>Detail of the Person </label> */}
            <div className="form-control">
            <label>Person Name</label>
            <input type="text" name="personName" value={personDetail.personName} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label>Person Age</label>
            <input type="text" name="personAge" value={personDetail.personAge} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label>Aadhar/Pan Number</label>
            <input type="text" name="personId" value={personDetail.personId} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label>Relation With Applicant</label>
            <input type="text" name="personRelation" value={personDetail.personRelation} onChange={personHandler} />
            </div>
        </div>
        <div className="form-actions">
           <button>Submit</button>
           </div>   
        </form>        
    </div>
  )
}

export default Form1;
