import React,{useState} from 'react';
import "./form.css";

function Login() {

    const [userLoginDetail,setUserLoginDetail]=useState({
        useremail: "",
        password: ""
    });

    const userHandler=(event)=>{
        const {name,value}=event.target;
        setUserLoginDetail((prevValue)=>{
            return {
                ...prevValue,
                [name]: value
            }
            
        });
    }

    const loginSubmitHandler=(event)=>{
        event.preventDefault();
        console.log(userLoginDetail.useremail);
        console.log(userLoginDetail.password);
    }
  return (
    <div className="form-handler">
        <form onSubmit={loginSubmitHandler}>
        <div className="control-group">
           <div className="form-control">
              <label>Email</label>
              <input type="email" name="username" value={userLoginDetail.useremail} onChange={userHandler} />
             </div> 
             <div className="form-control">
              <label>Password</label>
              <input type="text" name="password" value={userLoginDetail.password} onChange={userHandler} />
            </div> 
            </div>
            <div className="form-actions">
           <button>Submit</button>
           </div>  
            </form>
            </div>
  )
}

export default Login;
