import React,{useState} from 'react';
// import { DateRangePicker } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // main style file
import 'react-date-range/dist/theme/default.css'; // theme css file
import "./form.css";

function Form1() {

    // const selectionRange = {
    //     startDate: new Date(),
    //     endDate: new Date(),
    //     key: 'selection',
    //   };

      const [empDetail,setEmpDetail]=useState({
           empName: "",
           empPost: "",
           empId: "",
           city: "",
           district: "",
           state: "",
           pincode: "",
           mobile: "",
           purpose: ""
      });

      const [dateDetails,setDateDetails]=useState({
        startDate: "",
        endDate: ""
      });

      const [floor,setFloor]=useState("");
      const [personDetail,setPersonDetail]=useState({
            personName: "",
            personAge: "",
            personId: "",
            personRelation: ""
      });

    const empHandler=(event)=>{
        const {name,value}=event.target;
        setEmpDetail((prevValue)=> {
            return {
            ...prevValue,
            [name]: value
            };
        });
    }

    const dateHandler=(event)=>{
        const {name,value}=event.target;
              setDateDetails((prevValue)=> {
                return {
                    ...prevValue,
                    [name]: value
                }
              })
    }

    const floorHandler=(event)=>{
        setFloor(event.target.value);
    }

    const personHandler=(event)=>{
        const {name,value}=event.target;
              setPersonDetail((prevValue)=> {
                return {
                    ...prevValue,
                    [name]: value
                }
              })
    }
  return (
    <div className="form-handler">
        <form>
        <div className="control-group">
           <div className="form-control">
              <label htmlFor="name">Employee Name</label>
              <input id="employee" type="text" name="empName" value={empDetail.empName} onChange={empHandler} />
             </div> 
             <div className="form-control">
              <label htmlFor="post">Designation:</label>
              <input id="post" type="text" name="empPost" value={empDetail.empPost} onChange={empHandler} />
            </div> 

            <div className="form-control">
            <label htmlFor="id">Employee Id:</label>
            <input id="id" type="text" name="empId" value={empDetail.empId} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="mobile" >Mobile No:</label>
            <input id="mobile" type="text" name="mobile" value={empDetail.mobile} onChange={empHandler} />
           </div>
           <div className="form-control">
            <label htmlFor="purpose" >Purpose</label>
            <input id="purpose" type="text" name="purpose" value={empDetail.purpose} onChange={empHandler} />
            </div>
            {/* <label>Current Posting Address:</label> */}
            <div className="form-control">
            <label htmlFor="city">City:</label>
            <input id="city" type="text" name="city" value={empDetail.city} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="district" >District</label>
            <input id="district" type="text" name="district" value={empDetail.district} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="state" >State</label>
            <input id="state" type="text" name="state" value={empDetail.state} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="pincode" >Pincode</label>
            <input id="pincode" type="text" name="pincode" value={empDetail.pincode} onChange={empHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="startDate" >Start Date</label>
            <input id="startDate" type="date" name="startDate" value={dateDetails.startDate} onChange={dateHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="endDate" >End Date</label>
            <input id="endDate" type="date" name="endDate" value={dateDetails.endDate} onChange={dateHandler} />
            {/* <DateRangePicker
                ranges={[selectionRange]}
                // onChange={handleSelectRange}
            /> */}
            
            </div>
            {/* <label>Select Floor</label> */}
            <div className="form-control">
            <label htmlFor="hall">Hall  (Ground Floor):</label>
            <input id="hall" type="radio" name="selectFloor" value="hall" onChange={floorHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="lawn">Lawn (First Floor):</label>
            <input id="lawn" type="radio" name="selectFloor" value="lawn" onChange={floorHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="both">Both Hall and Lawn</label>
            <input id="both" type="radio" name="selectFloor" value="both" onChange={floorHandler} />
            </div>
            
            {/* <label>Detail of the Person </label> */}
            <div className="form-control">
            <label htmlFor="name">Person Name</label>
            <input id="employee" type="text" name="personName" value={personDetail.personName} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="age">Person Age</label>
            <input id="age" type="text" name="personAge" value={personDetail.personAge} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="identification">Aadhar/Pan Number</label>
            <input id="identificatin" type="text" name="personId" value={personDetail.personId} onChange={personHandler} />
            </div>
            <div className="form-control">
            <label htmlFor="relation">Relation With Applicant</label>
            <input id="relation" type="text" name="personRelation" value={personDetail.personRelation} onChange={personHandler} />
            </div>
        </div>
        <div className="form-actions">
           <button>Submit</button>
           </div>   
        </form>        
    </div>
  )
}

export default Form1;
